from django.shortcuts import render
import random

# Create your views here.
def evenodd(request, num):

    if num == 0:
        num_message = f"{num}은 0입니다"
    elif num % 2 == 0:
        num_message = f"{num}은 짝수입니다"
    else:
        num_message = f"{num}은 홀수입니다"

    context = {
        "num": num_message,
    }
    return render(request, "evenodd.html", context)


def cal(request, num1, num2):

    context = {
        "cal1": f"{num1} + {num2} = {num1 + num2}",
        "cal2": f"{num1} - {num2} = {num1 - num2}",
        "cal3": f"{num1} * {num2} = {num1 * num2}",
        "cal4": f"{num1} // {num2} = {num1 // num2}",
    }

    return render(request, "cal.html", context)


