def kloremresult(request):
    para = int(request.GET.get("para"))
    words = int(request.GET.get("words"))

    word = ["짜장면", "냉면", "라면", "삼겹살", "짬뽕", "한우", "불고기"]

    result = []

    for _ in range(para):
        temp = []
        for _ in range(words):
            temp.append(random.choice(word))
        result.append(temp)

    context = {
        "result": result,
    }

    print(result)

    return render(request, "kloremresult.html", context)
